import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom'; // Add this import

const PatientDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate(); // Add this
  const [patient, setPatient] = useState(null);
  const [appointments, setAppointments] = useState([]);

  // Add logout handler for any logout buttons in the dashboard
  const handleLogout = () => {
    logout();
    navigate('/'); // Redirect to home
  };

  useEffect(() => {
    fetchPatientData();
    fetchAppointments();
  }, []);

  const fetchPatientData = async () => {
    try {
      // Your existing data fetching code...
    } catch (error) {
      console.error('Error fetching patient data:', error);
    }
  };

  const fetchAppointments = async () => {
    try {
      // Your existing data fetching code...
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1>Welcome, {patient?.fullName || user?.username}</h1>
        <div className="dashboard-actions">
          <button 
            className="btn"
            onClick={() => navigate('/book-appointment')}
          >
            <i className="fas fa-plus"></i> Book Appointment
          </button>
          {/* Optional: Add logout button in dashboard too */}
          <button 
            className="btn btn-danger ml-2"
            onClick={handleLogout}
          >
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
      </div>

      {/* Rest of your dashboard content */}
      <div className="dashboard-grid">
        <div className="dashboard-card">
          <h2><i className="fas fa-calendar-alt"></i> Upcoming Appointments</h2>
          <div className="appointment-list">
            {/* Your appointments list */}
          </div>
        </div>

        <div className="dashboard-card">
          <h2><i className="fas fa-user-injured"></i> Patient Information</h2>
          {patient && (
            <div className="patient-info">
              <p><strong>Patient ID:</strong> {patient.patientId}</p>
              <p><strong>Age:</strong> {patient.age}</p>
              <p><strong>Gender:</strong> {patient.gender}</p>
              <p><strong>Blood Group:</strong> {patient.bloodGroup || 'Not specified'}</p>
              <p><strong>Phone:</strong> {patient.contact?.phone}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PatientDashboard;
