import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const PatientDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [patient, setPatient] = useState(null);
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    fetchPatientData();
    fetchAppointments();
  }, [user, navigate]);

  const fetchPatientData = async () => {
    try {
      const response = await api.get('/patients/profile/me');
      setPatient(response.data.data.patient);
    } catch (error) {
      console.error('Error fetching patient data:', error);
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await api.get('/appointments/my-appointments');
      setAppointments(response.data.data.appointments);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching appointments:', error);
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'short',
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getUpcomingAppointments = () => {
    const today = new Date();
    return appointments
      .filter(apt => new Date(apt.date) >= today && apt.status !== 'Cancelled')
      .sort((a, b) => new Date(a.date) - new Date(b.date))
      .slice(0, 3);
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'Confirmed':
        return 'status-badge confirmed';
      case 'Pending':
        return 'status-badge pending';
      case 'Completed':
        return 'status-badge completed';
      case 'Cancelled':
        return 'status-badge cancelled';
      default:
        return 'status-badge pending';
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading-state">
          <i className="fas fa-spinner fa-spin fa-2x"></i>
          <p>Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      {/* Dashboard Header */}
      <div className="dashboard-header">
        <div>
          <h1>Welcome, <span>{patient?.fullName || user?.username}</span></h1>
          <p>Here's your health dashboard and upcoming appointments</p>
        </div>
        <div className="dashboard-actions">
          <button 
            className="btn"
            onClick={() => navigate('/book-appointment')}
          >
            <i className="fas fa-calendar-plus"></i> Book Appointment
          </button>
          <button 
            className="btn btn-outline ml-2"
            onClick={() => navigate('/billing')}
          >
            <i className="fas fa-file-invoice-dollar"></i> View Bills
          </button>
        </div>
      </div>

      {/* Dashboard Navigation Tabs */}
      <div className="dashboard-sections">
        <button 
          className={`section-btn ${activeTab === 'overview' ? 'active' : ''}`}
          onClick={() => setActiveTab('overview')}
        >
          <i className="fas fa-home"></i> Overview
        </button>
        <button 
          className={`section-btn ${activeTab === 'appointments' ? 'active' : ''}`}
          onClick={() => setActiveTab('appointments')}
        >
          <i className="fas fa-calendar-alt"></i> Appointments
        </button>
        <button 
          className={`section-btn ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => setActiveTab('profile')}
        >
          <i className="fas fa-user"></i> Profile
        </button>
      </div>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        
        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="dashboard-section active">
            <div className="dashboard-grid">
              
              {/* Upcoming Appointments Card */}
              <div className="dashboard-card">
                <h2><i className="fas fa-calendar-check"></i> Upcoming Appointments</h2>
                <div className="appointment-list">
                  {getUpcomingAppointments().length > 0 ? (
                    getUpcomingAppointments().map(appointment => (
                      <div key={appointment._id} className="appointment-item">
                        <div className="appointment-time">
                          {formatDate(appointment.date)}
                          <br />
                          <small>{appointment.time}</small>
                        </div>
                        <div className="appointment-details">
                          <h3>Dr. {appointment.doctor?.name}</h3>
                          <p>{appointment.doctor?.specialization}</p>
                          <p className="appointment-purpose">{appointment.purpose}</p>
                          <span className={getStatusBadgeClass(appointment.status)}>
                            {appointment.status}
                          </span>
                        </div>
                        <button className="btn btn-outline btn-sm">
                          View Details
                        </button>
                      </div>
                    ))
                  ) : (
                    <div className="empty-appointments">
                      <i className="fas fa-calendar-times"></i>
                      <p>No upcoming appointments</p>
                      <button 
                        className="btn btn-sm"
                        onClick={() => navigate('/book-appointment')}
                      >
                        Book Your First Appointment
                      </button>
                    </div>
                  )}
                </div>
              </div>

              {/* Patient Information Card */}
              <div className="dashboard-card">
                <h2><i className="fas fa-user-injured"></i> Patient Information</h2>
                {patient && (
                  <div className="patient-info-details">
                    <div className="info-row">
                      <span className="info-label">Patient ID:</span>
                      <span className="info-value">{patient.patientId}</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Full Name:</span>
                      <span className="info-value">{patient.fullName}</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Age:</span>
                      <span className="info-value">{patient.age} years</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Gender:</span>
                      <span className="info-value">{patient.gender}</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Blood Group:</span>
                      <span className="info-value">{patient.bloodGroup || 'Not specified'}</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Phone:</span>
                      <span className="info-value">{patient.contact?.phone}</span>
                    </div>
                    <div className="info-row">
                      <span className="info-label">Email:</span>
                      <span className="info-value">{patient.contact?.email}</span>
                    </div>
                    {patient.address && (
                      <div className="info-row">
                        <span className="info-label">Address:</span>
                        <span className="info-value">
                          {patient.address.street}, {patient.address.city}, {patient.address.state} {patient.address.zipCode}
                        </span>
                      </div>
                    )}
                  </div>
                )}
                <button 
                  className="btn btn-outline mt-3"
                  onClick={() => setActiveTab('profile')}
                >
                  <i className="fas fa-edit"></i> Update Profile
                </button>
              </div>

              {/* Quick Actions Card */}
              <div className="dashboard-card">
                <h2><i className="fas fa-bolt"></i> Quick Actions</h2>
                <div className="quick-actions-grid">
                  <button 
                    className="quick-action-btn"
                    onClick={() => navigate('/book-appointment')}
                  >
                    <i className="fas fa-calendar-plus"></i>
                    <span>Book Appointment</span>
                  </button>
                  <button 
                    className="quick-action-btn"
                    onClick={() => navigate('/billing')}
                  >
                    <i className="fas fa-file-invoice"></i>
                    <span>View Bills</span>
                  </button>
                  <button 
                    className="quick-action-btn"
                    onClick={() => setActiveTab('profile')}
                  >
                    <i className="fas fa-user-edit"></i>
                    <span>Update Profile</span>
                  </button>
                  <button 
                    className="quick-action-btn"
                    onClick={() => window.print()}
                  >
                    <i className="fas fa-print"></i>
                    <span>Print Records</span>
                  </button>
                </div>
              </div>

              {/* Recent Activity Card */}
              <div className="dashboard-card">
                <h2><i className="fas fa-history"></i> Recent Activity</h2>
                <div className="activity-list">
                  {appointments.slice(0, 5).map(appointment => (
                    <div key={appointment._id} className="activity-item">
                      <div className="activity-icon">
                        <i className="fas fa-calendar"></i>
                      </div>
                      <div className="activity-content">
                        <p>
                          <strong>{appointment.purpose}</strong> with Dr. {appointment.doctor?.name}
                        </p>
                        <small>
                          {formatDate(appointment.date)} at {appointment.time} • 
                          <span className={getStatusBadgeClass(appointment.status)}>
                            {appointment.status}
                          </span>
                        </small>
                      </div>
                    </div>
                  ))}
                  {appointments.length === 0 && (
                    <div className="empty-activity">
                      <i className="fas fa-clock"></i>
                      <p>No recent activity</p>
                    </div>
                  )}
                </div>
              </div>

            </div>
          </div>
        )}

        {/* Appointments Tab */}
        {activeTab === 'appointments' && (
          <div className="dashboard-section active">
            <div className="section-header">
              <h2>My Appointments</h2>
              <button 
                className="btn"
                onClick={() => navigate('/book-appointment')}
              >
                <i className="fas fa-plus"></i> New Appointment
              </button>
            </div>
            
            {appointments.length > 0 ? (
              <div className="appointments-table">
                <div className="table-header">
                  <div>Date & Time</div>
                  <div>Doctor</div>
                  <div>Purpose</div>
                  <div>Status</div>
                  <div>Actions</div>
                </div>
                {appointments.map(appointment => (
                  <div key={appointment._id} className="table-row">
                    <div>
                      <strong>{formatDate(appointment.date)}</strong>
                      <br />
                      <small>{appointment.time}</small>
                    </div>
                    <div>
                      <strong>Dr. {appointment.doctor?.name}</strong>
                      <br />
                      <small>{appointment.doctor?.specialization}</small>
                    </div>
                    <div>{appointment.purpose}</div>
                    <div>
                      <span className={getStatusBadgeClass(appointment.status)}>
                        {appointment.status}
                      </span>
                    </div>
                    <div>
                      <button className="btn-icon" title="View Details">
                        <i className="fas fa-eye"></i>
                      </button>
                      {appointment.status === 'Pending' && (
                        <button className="btn-icon danger" title="Cancel Appointment">
                          <i className="fas fa-times"></i>
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <i className="fas fa-calendar-times fa-3x"></i>
                <h3>No Appointments Yet</h3>
                <p>You haven't booked any appointments yet.</p>
                <button 
                  className="btn"
                  onClick={() => navigate('/book-appointment')}
                >
                  <i className="fas fa-calendar-plus"></i> Book Your First Appointment
                </button>
              </div>
            )}
          </div>
        )}

        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="dashboard-section active">
            <div className="section-header">
              <h2>My Profile</h2>
              <button className="btn btn-outline">
                <i className="fas fa-edit"></i> Edit Profile
              </button>
            </div>
            
            {patient && (
              <div className="profile-card">
                <div className="profile-header">
                  <div className="profile-avatar">
                    <i className="fas fa-user fa-2x"></i>
                  </div>
                  <div className="profile-info">
                    <h3>{patient.fullName}</h3>
                    <p>Patient ID: {patient.patientId}</p>
                    <p>Member since {new Date(patient.createdAt).toLocaleDateString()}</p>
                  </div>
                </div>
                
                <div className="profile-details">
                  <div className="detail-section">
                    <h4>Personal Information</h4>
                    <div className="details-grid">
                      <div className="detail-item">
                        <label>Full Name</label>
                        <p>{patient.fullName}</p>
                      </div>
                      <div className="detail-item">
                        <label>Age</label>
                        <p>{patient.age} years</p>
                      </div>
                      <div className="detail-item">
                        <label>Gender</label>
                        <p>{patient.gender}</p>
                      </div>
                      <div className="detail-item">
                        <label>Blood Group</label>
                        <p>{patient.bloodGroup || 'Not specified'}</p>
                      </div>
                    </div>
                  </div>
                  
                  <div className="detail-section">
                    <h4>Contact Information</h4>
                    <div className="details-grid">
                      <div className="detail-item">
                        <label>Email</label>
                        <p>{patient.contact?.email}</p>
                      </div>
                      <div className="detail-item">
                        <label>Phone</label>
                        <p>{patient.contact?.phone}</p>
                      </div>
                      {patient.address && (
                        <>
                          <div className="detail-item">
                            <label>Address</label>
                            <p>{patient.address.street}</p>
                          </div>
                          <div className="detail-item">
                            <label>City</label>
                            <p>{patient.address.city}</p>
                          </div>
                          <div className="detail-item">
                            <label>State</label>
                            <p>{patient.address.state}</p>
                          </div>
                          <div className="detail-item">
                            <label>ZIP Code</label>
                            <p>{patient.address.zipCode}</p>
                          </div>
                        </>
                      )}
                    </div>
                  </div>
                  
                  <div className="detail-section">
                    <h4>Medical Information</h4>
                    <div className="details-grid">
                      <div className="detail-item">
                        <label>Status</label>
                        <p>{patient.status}</p>
                      </div>
                      <div className="detail-item">
                        <label>Last Updated</label>
                        <p>{new Date(patient.updatedAt).toLocaleDateString()}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

      </div>
    </div>
  );
};

export default PatientDashboard;
