import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const ManagePatients = () => {
  const { user } = useAuth();
  const [patients, setPatients] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({
    fullName: '', age: '', gender: 'Male', phone: '', email: '', password: '',
    addressStreet: '', addressCity: '', addressState: '', addressZip: '',
    bloodGroup: '', assignedDoctor: ''
  });
  const [editingId, setEditingId] = useState(null);
  const [accessDenied, setAccessDenied] = useState(false);

  useEffect(() => {
    const load = async () => {
                if (!user) return;
                if (!['admin', 'doctor', 'staff'].includes(user.role)) {
                  setAccessDenied(true);
                  return;
                }
                setLoading(true);
                try {
                  const [pRes, dRes] = await Promise.all([api.get('/patients'), api.get('/doctors')]);
                  setPatients(pRes.data.data.patients || []);
                  setDoctors(dRes.data.data.doctors || []);
                } catch (err) {
                  console.error(err);
                  alert(err?.response?.data?.message || 'Error loading data');
                } finally { setLoading(false); }
              };
              load();
            }, [user]);

            const handleChange = (e) => setForm(f => ({ ...f, [e.target.name]: e.target.value }));

            const generatePatientId = () => {
              const timestamp = Date.now().toString().slice(-4);
              return `P${timestamp}${Math.floor(Math.random() * 1000)}`;
            };

            const submitPayload = () => ({
              fullName: form.fullName.trim(),
              age: parseInt(form.age, 10),
              gender: form.gender,
              phone: form.phone.trim(),
              email: form.email.trim(),
              password: !editingId ? form.password : undefined, // Only send password for new patients
              address: {
                street: form.addressStreet ? form.addressStreet.trim() : '',
                city: form.addressCity ? form.addressCity.trim() : '',
                state: form.addressState ? form.addressState.trim() : '',
                zipCode: form.addressZip ? form.addressZip.trim() : ''
              },
              bloodGroup: form.bloodGroup ? form.bloodGroup.trim() : undefined
            });

            const validateForm = () => {
              const requiredFields = ['fullName', 'age', 'gender', 'email', 'phone'];
              if (!editingId) requiredFields.push('password'); // Password required only for new patients
              
              const missingFields = requiredFields.filter(field => !form[field]);
              if (missingFields.length > 0) {
                alert(`Please fill in the required fields: ${missingFields.join(', ')}`);
                return false;
              }

              // Validate age is a number and within reasonable range
              const age = parseInt(form.age, 10);
              if (isNaN(age) || age <= 0 || age > 150) {
                alert('Please enter a valid age between 1 and 150');
                return false;
              }

              // Validate email format
              const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
              if (!emailRegex.test(form.email)) {
                alert('Please enter a valid email address');
                return false;
              }

              // Validate phone number (basic validation)
              if (form.phone.length < 10) {
                alert('Please enter a valid phone number');
                return false;
              }

              // Validate password for new patients
              if (!editingId && form.password.length < 6) {
                alert('Password must be at least 6 characters long');
                return false;
              }

              return true;
            };

            const handleSubmit = async (e) => {
              e.preventDefault();
              if (!validateForm()) return;
              
              try {
                const payload = submitPayload();
                console.log('Submitting patient data:', payload);
                
                if (editingId) await api.patch(`/patients/${editingId}`, payload);
                else await api.post('/patients', submitPayload());
                setForm({ fullName: '', age: '', gender: 'Male', phone: '', email: '', addressStreet: '', addressCity: '', addressState: '', addressZip: '', bloodGroup: '', assignedDoctor: '' });
                const res = await api.get('/patients');
                setPatients(res.data.data.patients || []);
                setEditingId(null);
              } catch (err) {
                console.error('Error submitting patient:', err);
                if (err.response?.status === 401) {
                  alert('You are not authorized to perform this action. Please log in with admin or staff credentials.');
                } else if (err.response?.status === 400) {
                  const message = err.response.data.message || 'Please check all required fields';
                  alert(`Validation Error: ${message}`);
                  console.log('Server response:', err.response.data);
                } else {
                  alert('An error occurred while saving the patient. Please try again.');
                }
              }
            };

            const onEdit = (p) => {
              setEditingId(p._id || p.id);
              setForm({
                fullName: p.fullName || '', age: p.age || '', gender: p.gender || 'Male', phone: p.contact?.phone || '', email: p.user?.email || '',
                addressStreet: p.address?.street || '', addressCity: p.address?.city || '', addressState: p.address?.state || '', addressZip: p.address?.zipCode || '',
                bloodGroup: p.bloodGroup || '', assignedDoctor: p.assignedDoctor?._id || p.assignedDoctor || ''
              });
            };

            const onDelete = async (id) => {
              if (!window.confirm('Delete this patient?')) return;
              try { await api.delete(`/patients/${id}`); setPatients(patients.filter(x => (x._id || x.id) !== id)); } catch (err) { console.error(err); alert('Delete failed'); }
            };

            if (accessDenied) return <div className="card-compact"><p style={{ padding: 20 }}>You do not have permission to view patients.</p></div>;

            return (
              <div className="container admin-dashboard">
                <div className="admin-top"><h2>Manage Patients</h2></div>
                <div className="admin-main">
                  <div>
                    <div className="card-compact">
                      <h3>Patients List</h3>
                      {loading ? <p>Loading...</p> : (
                        <table className="manage-table">
                          <thead>
                            <tr><th>Name</th><th>Age</th><th>Gender</th><th>Email</th><th>Contact</th><th>Address</th><th>Doctor</th><th>Actions</th></tr>
                          </thead>
                          <tbody>
                            {patients.map(p => (
                              <tr key={p._id || p.id}>
                                <td>{p.fullName}</td>
                                <td>{p.age}</td>
                                <td>{p.gender}</td>
                                <td>{p.user?.email || '-'}</td>
                                <td>{p.contact?.phone || '-'}</td>
                                <td>{p.address ? `${p.address.street || ''}${p.address.city ? ', ' + p.address.city : ''}${p.address.state ? ', ' + p.address.state : ''}${p.address.zipCode ? ' ' + p.address.zipCode : ''}` : '-'}</td>
                                <td>{p.assignedDoctor ? (typeof p.assignedDoctor === 'string' ? <Link to={`/doctors/${p.assignedDoctor}`}>View</Link> : <Link to={`/doctors/${p.assignedDoctor._id || p.assignedDoctor.id}`}>{p.assignedDoctor.name || p.assignedDoctor.email || 'View'}</Link>) : '-'}</td>
                                <td>
                                  <button className="action-btn view" onClick={() => onEdit(p)}>Edit</button>
                                  <button className="action-btn delete" onClick={() => onDelete(p._id || p.id)} style={{ marginLeft: 8 }}>Delete</button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      )}
                    </div>
                  </div>
                  <aside className="card-compact profile-panel">
                    <h3>{editingId ? 'Edit Patient' : 'Add Patient'}</h3>
                    <form onSubmit={handleSubmit}>
                      <div className="form-group"><label>Full Name</label><input className="form-control" name="fullName" value={form.fullName} onChange={handleChange} required /></div>
                      <div className="form-group"><label>Age</label><input className="form-control" type="number" name="age" value={form.age} onChange={handleChange} /></div>
                      <div className="form-group"><label>Gender</label><select className="form-control" name="gender" value={form.gender} onChange={handleChange}><option>Male</option><option>Female</option><option>Other</option></select></div>
                      <div className="form-group"><label>Phone</label><input className="form-control" name="phone" value={form.phone} onChange={handleChange} /></div>
                      <div className="form-group"><label>Email</label><input className="form-control" name="email" value={form.email} onChange={handleChange} /></div>
                      <div className="form-group"><label>Street</label><input className="form-control" name="addressStreet" value={form.addressStreet} onChange={handleChange} /></div>
                      <div className="form-group"><label>City</label><input className="form-control" name="addressCity" value={form.addressCity} onChange={handleChange} /></div>
                      <div className="form-group"><label>State</label><input className="form-control" name="addressState" value={form.addressState} onChange={handleChange} /></div>
                      <div className="form-group"><label>Zip</label><input className="form-control" name="addressZip" value={form.addressZip} onChange={handleChange} /></div>
                      <div className="form-group"><label>Blood Group</label><input className="form-control" name="bloodGroup" value={form.bloodGroup} onChange={handleChange} /></div>
                      <div className="form-group"><label>Assigned Doctor</label><select className="form-control" name="assignedDoctor" value={form.assignedDoctor} onChange={handleChange}><option value="">Unassigned</option>{doctors.map(d => <option key={d._id} value={d._id}>{d.name}{d.specialization ? ` (${d.specialization})` : ''}</option>)}</select></div>
                      <div style={{ display: 'flex', gap: 8 }}>
                        <button className="btn" type="submit">{editingId ? 'Update' : 'Add'} Patient</button>
                        {editingId && <button type="button" className="btn" onClick={() => { setEditingId(null); setForm({ fullName: '', age: '', gender: 'Male', phone: '', email: '', addressStreet: '', addressCity: '', addressState: '', addressZip: '', bloodGroup: '', assignedDoctor: '' }); }}>Cancel</button>}
                      </div>
                    </form>
                  </aside>
                </div>
              </div>
            );
          };

          export default ManagePatients;
