import React from 'react';
import { Link } from 'react-router-dom';

const servicesList = [
  // Primary Care & Emergency Services
  { title: 'Emergency Medicine', desc: '24/7 emergency care with trauma specialists.', icon: 'fas fa-ambulance', id: 'emergency' },
  { title: 'Primary Care', desc: 'Comprehensive family healthcare services.', icon: 'fas fa-user-md', id: 'primary' },
  { title: 'Critical Care', desc: 'Intensive care for critically ill patients.', icon: 'fas fa-procedures', id: 'critical' },
  
  // Specialty Centers
  { title: 'Cardiology & Heart Surgery', desc: 'Advanced cardiac care and surgical procedures.', icon: 'fas fa-heartbeat', id: 'cardiology' },
  { title: 'Neurology & Brain Surgery', desc: 'Complex neurological treatments and surgeries.', icon: 'fas fa-brain', id: 'neurology' },
  { title: 'Cancer Center', desc: 'Comprehensive cancer treatment and oncology services.', icon: 'fas fa-ribbon', id: 'oncology' },
  { title: 'Orthopedics', desc: 'Joint replacements and musculoskeletal care.', icon: 'fas fa-bone', id: 'orthopedics' },
  { title: 'Spine Center', desc: 'Advanced spine surgery and treatment.', icon: 'fas fa-user-injured', id: 'spine' },
  
  // Women's & Children's Health
  { title: 'Maternity Care', desc: 'Complete pregnancy and childbirth services.', icon: 'fas fa-baby-carriage', id: 'maternal' },
  { title: 'Women\'s Health', desc: 'Specialized care for women\'s health needs.', icon: 'fas fa-female', id: 'womens' },
  { title: 'Pediatrics', desc: 'Comprehensive children\'s healthcare.', icon: 'fas fa-baby', id: 'pediatrics' },
  { title: 'Neonatal ICU', desc: 'Intensive care for newborns.', icon: 'fas fa-baby', id: 'nicu' },
  
  // Surgical Services
  { title: 'Robotic Surgery', desc: 'Minimally invasive robotic procedures.', icon: 'fas fa-robot', id: 'robotics' },
  { title: 'Transplant Center', desc: 'Organ transplantation services.', icon: 'fas fa-heart', id: 'transplant' },
  { title: 'General Surgery', desc: 'Wide range of surgical procedures.', icon: 'fas fa-user-md', id: 'surgery' },
  
  // Specialized Care
  { title: 'Diabetes & Endocrinology', desc: 'Hormone and metabolic disorder treatment.', icon: 'fas fa-tablets', id: 'diabetes' },
  { title: 'Mental Health', desc: 'Psychiatric and psychological services.', icon: 'fas fa-brain', id: 'mental' },
  { title: 'Rehabilitation', desc: 'Physical and occupational therapy.', icon: 'fas fa-running', id: 'rehab' },
  
  // Diagnostic & Support
  { title: 'Advanced Imaging', desc: 'MRI, CT, PET scans and radiology.', icon: 'fas fa-x-ray', id: 'imaging' },
  { title: 'Laboratory Services', desc: 'Comprehensive diagnostic testing.', icon: 'fas fa-vials', id: 'lab' },
  { title: 'Pharmacy', desc: '24/7 pharmaceutical services.', icon: 'fas fa-pills', id: 'pharmacy' },
  
  // Additional Specialties
  { title: 'Pulmonology', desc: 'Respiratory and lung care.', icon: 'fas fa-lungs', id: 'pulmonary' },
  { title: 'Gastroenterology', desc: 'Digestive system care.', icon: 'fas fa-stomach', id: 'gastro' },
  { title: 'Dental & Oral Surgery', desc: 'Complete dental and maxillofacial care.', icon: 'fas fa-tooth', id: 'dental' },
  { title: 'ENT', desc: 'Ear, nose, and throat specialty care.', icon: 'fas fa-head-side-mask', id: 'ent' }
];

const Services = () => {
  return (
    <div className="container">
      <div className="services-hero card text-center">
        <h2>All Medical Services</h2>
        <p className="muted">Explore our comprehensive healthcare services</p>
      </div>

      <div className="services-grid">
        {servicesList.map((s) => (
          <div key={s.title} className="service-card">
            <div className="service-icon"><i className={s.icon}></i></div>
            <h3>{s.title}</h3>
            <p>{s.desc}</p>
          </div>
        ))}
      </div>

      <div style={{ textAlign: 'center', marginTop: 24 }}>
        <Link to="/" className="btn btn-outline">Back to Home</Link>
      </div>
    </div>
  );
};

export default Services;
