import React, { useEffect, useState } from 'react';
import api from '../services/api';

const ManageDoctors = () => {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({ name: '', specialization: '', department: '', phone: '', email: '', password: '', consultationFee: '', qualification: '', experience: '' });
  const [specializations, setSpecializations] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [activeTab, setActiveTab] = useState('list'); // 'list' or 'add'

  // CSS styles for tabs
  const tabStyle = {
    container: {
      marginBottom: '20px',
      borderBottom: '1px solid #ddd'
    },
    tabs: {
      display: 'flex',
      gap: '10px',
      marginBottom: '-1px'
    },
    tab: {
      padding: '10px 20px',
      cursor: 'pointer',
      border: '1px solid transparent',
      borderRadius: '4px 4px 0 0',
      backgroundColor: '#f8f9fa'
    },
    activeTab: {
      backgroundColor: '#fff',
      border: '1px solid #ddd',
      borderBottom: '1px solid #fff',
      fontWeight: 'bold'
    }
  };

  const fetchDoctors = async () => {
    try {
      setLoading(true);
      const res = await api.get('/doctors');
      setDoctors(res.data.data.doctors);
    } catch (err) {
      console.error(err);
      alert('Error fetching doctors');
    } finally {
      setLoading(false);
    }
  };

  const fetchMeta = async () => {
    try {
      const sRes = await api.get('/specializations');
      const dRes = await api.get('/departments');
      setSpecializations(sRes.data.data.specializations || []);
      setDepartments(dRes.data.data.departments || []);
    } catch (err) {
      console.error('Error fetching meta:', err);
    }
  };

  useEffect(() => { fetchDoctors(); }, []);
  useEffect(() => { fetchMeta(); }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...form };
      // convert qualification string to array
      if (payload.qualification && typeof payload.qualification === 'string') {
        payload.qualification = payload.qualification.split(',').map(s => s.trim()).filter(Boolean);
      }
      if (payload.experience !== '') payload.experience = Number(payload.experience);

      if (editingId) {
        // only send provided fields
        const updatePayload = { ...payload };
        await api.patch(`/doctors/${editingId}`, updatePayload);
        setEditingId(null);
      } else {
        await api.post('/doctors', payload);
      }
      setForm({ name: '', specialization: '', department: '', phone: '', email: '', password: '', consultationFee: '', qualification: '', experience: '' });
      fetchDoctors();
    } catch (err) {
      console.error(err);
      alert('Error saving doctor');
    }
  };

  const handleEdit = (doc) => {
    setEditingId(doc._id);
  setForm({ name: doc.name, specialization: doc.specialization, department: doc.department, phone: doc.contact?.phone || '', email: doc.contact?.email || '', password: '', consultationFee: doc.consultationFee || '', qualification: Array.isArray(doc.qualification) ? doc.qualification.join(', ') : (doc.qualification || ''), experience: doc.experience || '' });
  };

  const toggleActive = async (doc) => {
    try {
      await api.patch(`/doctors/${doc._id}`, { isActive: !doc.isActive });
      fetchDoctors();
    } catch (err) {
      console.error('Error toggling active', err);
      alert('Failed to update status');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this doctor?')) return;
    try { await api.delete(`/doctors/${id}`); fetchDoctors(); } catch (err) { console.error(err); alert('Error deleting doctor'); }
  };

  return (
    <div className="container admin-dashboard" style={{ maxWidth: '2000px', margin: '0 auto' }}>
      <div className="admin-top">
        <h2>Manage Doctors</h2>
      </div>
      <div className="admin-main" style={{ width: '95%', margin: '0 auto' }}>
        <div style={tabStyle.container}>
          <div style={tabStyle.tabs}>
            <div
              style={{
                ...tabStyle.tab,
                ...(activeTab === 'list' ? tabStyle.activeTab : {})
              }}
              onClick={() => setActiveTab('list')}
            >
              Doctors List
            </div>
            <div
              style={{
                ...tabStyle.tab,
                ...(activeTab === 'add' ? tabStyle.activeTab : {})
              }}
              onClick={() => {
                setActiveTab('add');
                setEditingId(null);
                setForm({ 
                  name: '', 
                  specialization: '', 
                  department: '', 
                  phone: '', 
                  email: '', 
                  password: '', 
                  consultationFee: '', 
                  qualification: '', 
                  experience: '' 
                });
              }}
            >
              Add New Doctor
            </div>
          </div>
        </div>
        <div className="card-grid">
          <div className="card-col main-col" style={{ maxWidth: '900px', margin: '0 auto', padding: '0 15px' }}>
            {activeTab === 'list' ? (
              <div className="card-compact" style={{ overflowX: 'auto', width: '100%' }}>
                <h3>Doctors List</h3>
            {loading ? <p>Loading...</p> : (
              <table className="manage-table" style={{ width: '100%', fontSize: '0.9rem' }}>
                <thead>
                  <tr>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Doctor ID</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Name</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Email</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Specialization</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Department</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Experience</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Fee</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Availability</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Active</th>
                    <th style={{ padding: '12px 16px', borderBottom: '2px solid #ddd', background: '#f8f9fa', textAlign: 'left' }}>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {doctors.map(d => (
                    <tr key={d._id}>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.doctorId}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.name}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.contact?.email || d.user?.email}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.specialization}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.department}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.experience || 0} yrs</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>â‚¹{d.consultationFee}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{Array.isArray(d.availability?.days) ? d.availability.days.join(', ') : ''}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.password || d.user?.password ? 'Yes' : 'No'}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>{d.isActive ? 'Yes' : 'No'}</td>
                      <td style={{ padding: '12px 16px', borderBottom: '1px solid #eee' }}>
                        <button className="action-btn view" onClick={() => handleEdit(d)}>Edit</button>
                        <button className="action-btn" onClick={() => toggleActive(d)} style={{ marginLeft: 6 }}>{d.isActive ? 'Deactivate' : 'Activate'}</button>
                        <button className="action-btn delete" onClick={() => handleDelete(d._id)} style={{ marginLeft: 6 }}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
              </div>
            ) : (
              <div className="card-compact">
                <h3>{editingId ? 'Edit Doctor' : 'Add Doctor'}</h3>
                <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Name</label>
              <input className="form-control" name="name" value={form.name} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Specialization</label>
              <select className="form-control" name="specialization" value={form.specialization} onChange={handleChange} required>
                <option value="">Select Specialization</option>
                {specializations.map(s => <option key={s._id} value={s.name}>{s.name}</option>)}
              </select>
            </div>
            <div className="form-group">
              <label>Department</label>
              <select className="form-control" name="department" value={form.department} onChange={handleChange} required>
                <option value="">Select Department</option>
                {departments.map(d => <option key={d._id} value={d.name}>{d.name}</option>)}
              </select>
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input className="form-control" name="phone" value={form.phone} onChange={handleChange} />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input className="form-control" name="email" value={form.email} onChange={handleChange} required />
            </div>
            <div className="form-group">
              <label>Password (for doctor login)</label>
              <input className="form-control" type="password" name="password" value={form.password} onChange={handleChange} placeholder={editingId ? 'Leave blank to keep existing password' : ''} />
            </div>
            <div className="form-group">
              <label>Qualification(s) (comma separated)</label>
              <input className="form-control" name="qualification" value={form.qualification} onChange={handleChange} placeholder="e.g. MBBS, MD - Cardiology" />
            </div>
            <div className="form-group">
              <label>Experience (years)</label>
              <input className="form-control" name="experience" value={form.experience} onChange={handleChange} type="number" min="0" />
            </div>
            <div className="form-group">
              <label>Consultation Fee</label>
              <input className="form-control" name="consultationFee" value={form.consultationFee} onChange={handleChange} />
            </div>
            <button className="btn" type="submit">{editingId ? 'Update' : 'Add'} Doctor</button>
          </form>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManageDoctors;
