import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: 'patient'
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  
  const { login, error, clearError } = useAuth();
  const navigate = useNavigate();

  // Clear errors when component mounts
  useEffect(() => {
    clearError();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear error when user starts typing
    if (error) clearError();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Basic validation
    if (!formData.email || !formData.password) {
      alert('Please enter both email and password');
      setLoading(false);
      return;
    }

    console.log('Submitting login form...');

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      console.log('Login successful, redirecting...');
      // Redirect based on user role
      switch (result.data.user.role) {
        case 'patient':
          navigate('/patient-dashboard');
          break;
        case 'doctor':
          navigate('/doctor-dashboard');
          break;
        case 'staff':
          navigate('/staff-dashboard');
          break;
        case 'admin':
          navigate('/dashboard');
          break;
        default:
          navigate('/');
      }
    } else {
      console.log('Login failed:', result.message);
      alert(result.message || 'Login failed');
    }
    
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="card">
        <h2>Login to MediCare</h2>
        
        {error && (
          <div className="alert alert-danger">
            <i className="fas fa-exclamation-triangle"></i> 
            <strong> Error:</strong> {error}
          </div>
        )}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">Email Address *</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              required
              placeholder="Enter your email address"
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password *</label>
            <div className="password-input-container">
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="form-control"
                required
                placeholder="Enter your password"
                disabled={loading}
                minLength="6"
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                disabled={loading}
              >
                <i className={`fas fa-${showPassword ? 'eye-slash' : 'eye'}`}></i>
              </button>
            </div>
          </div>

          <div className="form-group">
            <label>I am a</label>
            <div className="user-types">
              <button 
                type="button"
                className={`user-type ${formData.userType === 'patient' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'patient'})}
                disabled={loading}
              >
                Patient
              </button>
              <button 
                type="button"
                className={`user-type ${formData.userType === 'doctor' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'doctor'})}
                disabled={loading}
              >
                Doctor
              </button>
              <button 
                type="button"
                className={`user-type ${formData.userType === 'staff' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'staff'})}
                disabled={loading}
              >
                Staff
              </button>
            </div>
          </div>

          <button 
            type="submit" 
            className="submit-btn"
            disabled={loading}
          >
            {loading ? (
              <>
                <i className="fas fa-spinner fa-spin"></i> Logging in...
              </>
            ) : (
              <>
                <i className="fas fa-sign-in-alt"></i> Login
              </>
            )}
          </button>
        </form>

        <div className="form-footer">
          <p>Don't have an account? <Link to="/patient-signup">Sign up as Patient</Link></p>
          <p className="forgot-password">
            <a href="#forgot">Forgot your password?</a>
          </p>
        </div>

        {/* Demo credentials hint */}
        <div className="demo-credentials">
          <h4>Demo Credentials:</h4>
          <p><strong>Email:</strong> demo@medicare.com</p>
          <p><strong>Password:</strong> demo123</p>
          <small>(You'll need to create an account first)</small>
        </div>
      </div>
    </div>
  );
};

export default Login;
