import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';

const ManagePatients = () => {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(false);
  const [form, setForm] = useState({ fullName: '', age: '', gender: 'Male', phone: '', email: '', address: '', bloodGroup: '', assignedDoctor: '' });
  const [editingId, setEditingId] = useState(null);
  const { user } = useAuth();
  const [accessDenied, setAccessDenied] = useState(false);
  const [doctors, setDoctors] = useState([]);

  const fetchPatients = async () => {
    try {
      setLoading(true);
      // ensure current user has rights client-side before calling
      if (!user || !['admin', 'doctor', 'staff'].includes(user.role)) {
        setAccessDenied(true);
        setLoading(false);
        return;
      }

      const res = await api.get('/patients');
      setPatients(res.data.data.patients);
    } catch (err) {
      console.error('Fetch patients error:', err);
      // Show server response if available
      if (err.response) {
        console.error('Response data:', err.response.data);
        if (err.response.status === 403) {
          alert('Access denied: you do not have permission to view patients.');
        } else {
          alert(err.response.data?.message || 'Error fetching patients');
        }
      } else {
        alert('Network or client error fetching patients');
      }
    } finally { setLoading(false); }
  };

  const fetchDoctors = async () => {
    try {
      const res = await api.get('/doctors');
      setDoctors(res.data.data.doctors || []);
    } catch (err) {
      console.error('Error fetching doctors for dropdown', err);
    }
  };

  useEffect(() => { fetchPatients(); fetchDoctors(); }, [user]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.patch(`/patients/${editingId}`, form);
        setEditingId(null);
      } else {
        await api.post('/patients', form);
      }
      setForm({ fullName: '', age: '', gender: 'Male', phone: '', email: '', address: '', bloodGroup: '', assignedDoctor: '' });
      fetchPatients();
    } catch (err) { console.error(err); alert('Error saving patient'); }
  };

  const handleEdit = (p) => {
    setEditingId(p._id);
    setForm({
      fullName: p.fullName,
      age: p.age,
      gender: p.gender,
      phone: p.contact?.phone || '',
      email: p.user?.email || '',
      address: p.address || '',
      bloodGroup: p.bloodGroup || '',
      assignedDoctor: p.assignedDoctor?._id || ''
    });
  };
  const handleDelete = async (id) => { if (!window.confirm('Delete this patient?')) return; try { await api.delete(`/patients/${id}`); fetchPatients(); } catch (err) { console.error(err); alert('Error deleting patient'); } };

  if (accessDenied) {
    return (
      <div className="container admin-dashboard">
        <div className="admin-top"><h2>Manage Patients</h2></div>
        <div className="card-compact"><p style={{ padding: 20 }}>You do not have permission to view or manage patients.</p></div>
      </div>
    );
  }

  return (
    <div className="container admin-dashboard">
      <div className="admin-top"><h2>Manage Patients</h2></div>
      <div className="admin-main">
        <div>
          <div className="card-compact">
            <h3>Patients List</h3>
            {loading ? <p>Loading...</p> : (
              <table className="manage-table">
                <thead><tr><th>Name</th><th>Age</th><th>Gender</th><th>Email</th><th>Contact</th><th>Address</th><th>Blood Group</th><th>Doctor</th><th>Actions</th></tr></thead>
                <tbody>
                  {patients.map(p => (
                    <tr key={p._id}>
                      <td>{p.fullName}</td>
                      <td>{p.age}</td>
                      <td>{p.gender}</td>
                      <td>{p.user?.email}</td>
                      <td>{p.contact?.phone}</td>
                      <td>{p.address || '-'}</td>
                      <td>{p.bloodGroup || '-'}</td>
                      <td>{p.assignedDoctor?.name || '-'}</td>
                      <td>
                        <button className="action-btn view" onClick={() => handleEdit(p)}>Edit</button>
                        <button className="action-btn delete" onClick={() => handleDelete(p._id)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        </div>
        <aside className="card-compact profile-panel">
          <h3>{editingId ? 'Edit Patient' : 'Add Patient'}</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group"><label>Full Name</label><input className="form-control" name="fullName" value={form.fullName} onChange={handleChange} required /></div>
            <div className="form-group"><label>Age</label><input className="form-control" type="number" name="age" value={form.age} onChange={handleChange} /></div>
            <div className="form-group"><label>Gender</label><select className="form-control" name="gender" value={form.gender} onChange={handleChange}><option>Male</option><option>Female</option><option>Other</option></select></div>
            <div className="form-group"><label>Phone</label><input className="form-control" name="phone" value={form.phone} onChange={handleChange} /></div>
            <div className="form-group"><label>Email</label><input className="form-control" name="email" value={form.email} onChange={handleChange} /></div>
            <div className="form-group"><label>Address</label><input className="form-control" name="address" value={form.address} onChange={handleChange} /></div>
            <div className="form-group"><label>Blood Group</label><input className="form-control" name="bloodGroup" value={form.bloodGroup} onChange={handleChange} /></div>
            <div className="form-group"><label>Assigned Doctor</label>
              <select className="form-control" name="assignedDoctor" value={form.assignedDoctor} onChange={handleChange}>
                <option value="">Unassigned</option>
                {doctors.map(d => <option key={d._id} value={d._id}>{d.name} ({d.specialization})</option>)}
              </select>
            </div>
            <button className="btn" type="submit">{editingId ? 'Update' : 'Add'} Patient</button>
          </form>
        </aside>
      </div>
    </div>
  );
};

export default ManagePatients;
