import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, Link } from 'react-router-dom';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: 'patient'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    const result = await login(formData.email, formData.password);
    
    if (result.success) {
      // Redirect based on user role
      switch (result.data.user.role) {
        case 'patient':
          navigate('/patient-dashboard');
          break;
        case 'doctor':
          navigate('/doctor-dashboard');
          break;
        case 'staff':
          navigate('/staff-dashboard');
          break;
        case 'admin':
          navigate('/dashboard');
          break;
        default:
          navigate('/');
      }
    } else {
      setError(result.message);
    }
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="card">
        <h2>Login to MediCare</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group">
            <label>Login as</label>
            <div className="user-types">
              <button 
                type="button"
                className={`user-type ${formData.userType === 'patient' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'patient'})}
              >
                Patient
              </button>
              <button 
                type="button"
                className={`user-type ${formData.userType === 'doctor' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'doctor'})}
              >
                Doctor
              </button>
              <button 
                type="button"
                className={`user-type ${formData.userType === 'staff' ? 'active' : ''}`}
                onClick={() => setFormData({...formData, userType: 'staff'})}
              >
                Staff
              </button>
            </div>
          </div>

          <button 
            type="submit" 
            className="submit-btn"
            disabled={loading}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div className="form-footer">
          <p>Don't have an account? <Link to="/patient-signup">Sign up as Patient</Link></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
