import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api';

// Create context with undefined default value
const AuthContext = createContext(undefined);

// Custom hook to use auth context
export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

// Provider component
export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = async () => {
      try {
        const token = localStorage.getItem('token');
        const userData = localStorage.getItem('user');
        
        if (!token || !userData) {
          setUser(null);
          setLoading(false);
          return;
        }

        // Set the user from stored data first to maintain state during refresh
        const parsedUser = JSON.parse(userData);
        setUser(parsedUser);
        
        // Set authorization header
        api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        // Validate token with the backend silently
        try {
          await api.get('/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
        } catch (error) {
          // Only logout on critical auth errors
          if (error.response?.status === 401) {
            console.error('Token invalid:', error);
            logout();
          }
          // For other errors, keep the user logged in
          console.error('Token validation error:', error);
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        // Don't logout on JSON parse or other non-auth errors
      } finally {
        setLoading(false);
      }
    };
    checkAuth();
  }, []);

  const login = async (email, password) => {
    try {
      const response = await api.post('/auth/login', { email, password });
      
      if (response.data.status === 'success') {
        const { token, data } = response.data;
        
        // Store auth data
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(data.user));
        
        // Update axios default headers
        api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        
        // Update state
        setUser(data.user);
        
        return { success: true, data };
      } else {
        throw new Error(response.data.message || 'Login failed');
      }
    } catch (error) {
      // Clear any partial data on error
      logout();
      return { 
        success: false, 
        message: error.response?.data?.message || error.message || 'Login failed' 
      };
    }
  };

  const logout = () => {
    // Clear localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    
    // Clear axios headers
    delete api.defaults.headers.common['Authorization'];
    
    // Reset state
    setUser(null);
    setLoading(false);
  };

  const value = {
    user,
    login,
    logout,
    loading
  };

  if (loading) return null;

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
